    candidate_id: candidate.candidate_id,
    candidate_name: candidate.name,
    candidate_phone: candidate.phone,
    candidate_email: candidate.email,
    scheduled_time: scheduledTime.toISOString(),
    status: 'scheduled',
    questions: interviewQuestions,
    ats_score: candidate.ats_score
  };

  scheduled.push(interview);

  // Mark candidate as interview scheduled
  const candidateIndex = candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    candidates[candidateIndex].interview_scheduled = true;
    candidates[candidateIndex].interview_id = interviewId;
    candidates[candidateIndex].interview_scheduled_time = scheduledTime.toISOString();
    candidates[candidateIndex].status = 'interview_scheduled';
  }
});

// Update candidates.json
fs.writeFileSync(candidatesPath, JSON.stringify(candidates, null, 2));

// Store scheduled interviews
const interviewsPath = '/tmp/interviews.json';
let interviews = [];

try {
  if (fs.existsSync(interviewsPath)) {
    interviews = JSON.parse(fs.readFileSync(interviewsPath, 'utf8'));
  }
} catch (error) {
  interviews = [];
}

interviews.push(...scheduled);
fs.writeFileSync(interviewsPath, JSON.stringify(interviews, null, 2));

return scheduled.map(interview => ({ json: { interview } }));

// Process interview results and calculate interview score
const fs = require('fs');

// Get interview data
const interviewData = $input.first().json.interview;
const callResponse = $input.first().json;

// Simulate interview processing (in real scenario, this would get transcription from Retell)
const mockResponses = [
  "I have 5 years of experience in software development, working with various technologies like React and Node.js.",
  "I'm interested in this position because it aligns with my career goals and I admire the company's innovative approach.",
  "My greatest strength is problem-solving and my ability to work effectively in team environments.",
  "I once faced a critical bug in production and managed to identify and fix it within 2 hours through systematic debugging.",
  "In 5 years, I see myself in a senior technical role, leading projects and mentoring junior developers."
];

// Score each response using Groq (simulated for now)
const responses = interviewData.questions.map((q, index) => {
  const response = mockResponses[index] || "Candidate provided a brief response.";

  // Calculate score based on keyword matching and length
  let score = 0;
  const responseWords = response.toLowerCase().split(/\s+/);
  const matchedKeywords = q.expected_keywords.filter(keyword => 
    responseWords.some(word => word.includes(keyword.toLowerCase()))
  );

  // Base score from keyword matching
  score = Math.min((matchedKeywords.length / q.expected_keywords.length) * q.max_score, q.max_score);

  // Bonus for response length (indicates detail)
  if (response.length > 50) score += 2;
  if (response.length > 100) score += 2;

  return {
    question: q.question,
    response: response,
    expected_keywords: q.expected_keywords,
    matched_keywords: matchedKeywords,
    score: Math.round(score),
    max_score: q.max_score
  };
});

// Calculate overall interview score
const totalScore = responses.reduce((sum, r) => sum + r.score, 0);
const maxPossibleScore = responses.reduce((sum, r) => sum + r.max_score, 0);
const interviewScore = Math.round((totalScore / maxPossibleScore) * 100);

// Communication and technical scores
const communicationScore = Math.min(interviewScore + Math.floor(Math.random() * 10), 100);
const technicalScore = Math.min(interviewScore + Math.floor(Math.random() * 15), 100);

const interviewResult = {
  interview_id: interviewData.interview_id,
  candidate_id: interviewData.candidate_id,
  call_id: callResponse.call_id || `simulated_call_${Date.now()}`,
  call_status: callResponse.status || 'completed',
  interview_date: new Date().toISOString(),
  responses: responses,
  communication_score: communicationScore,
  technical_score: technicalScore,
  overall_interview_score: interviewScore,
  total_possible_score: maxPossibleScore,
  actual_score: totalScore
};

// Store interview results
const interviewsPath = '/tmp/interviews.json';
let interviews = JSON.parse(fs.readFileSync(interviewsPath, 'utf8'));
const interviewIndex = interviews.findIndex(i => i.interview_id === interviewData.interview_id);
if (interviewIndex !== -1) {
  interviews[interviewIndex] = { ...interviews[interviewIndex], ...interviewResult };
  interviews[interviewIndex].status = 'completed';
  fs.writeFileSync(interviewsPath, JSON.stringify(interviews, null, 2));
}

// Update candidate with interview score
const candidatesPath = '/tmp/candidates.json';
let candidates = JSON.parse(fs.readFileSync(candidatesPath, 'utf8'));
const candidateIndex = candidates.findIndex(c => c.candidate_id === interviewData.candidate_id);
if (candidateIndex !== -1) {
  candidates[candidateIndex].interview_score = interviewScore;
  candidates[candidateIndex].interview_completed = true;
  candidates[candidateIndex].interview_date = interviewResult.interview_date;
  candidates[candidateIndex].status = 'interviewed';

  // Calculate combined score (ATS 60% + Interview 40%)
  const atsScore = candidates[candidateIndex].ats_score || 0;
  const combinedScore = Math.round((atsScore * 0.6) + (interviewScore * 0.4));
  candidates[candidateIndex].combined_score = combinedScore;

  fs.writeFileSync(candidatesPath, JSON.stringify(candidates, null, 2));
}

// Log interview completion
const logEntry = `${new Date().toISOString()} - Interview completed: ${interviewData.candidate_name} - Score: ${interviewScore}/100\n`;
fs.appendFileSync('/tmp/interview_log.txt', logEntry);

return [{
  json: {
    success: true,
    interview_id: interviewResult.interview_id,
    candidate_id: interviewResult.candidate_id,
    interview_score: interviewScore,
    interview_result: interviewResult
  }
}];

// Select top 20 candidates based on combined ATS + Interview scores
const fs = require('fs');

// Load candidates
const candidatesPath = '/tmp/candidates.json';
let candidates = JSON.parse(fs.readFileSync(candidatesPath, 'utf8'));

// Filter candidates who completed interviews
const interviewedCandidates = candidates.filter(c => 
  c.status === 'interviewed' && c.combined_score !== undefined && c.combined_score > 0
);

if (interviewedCandidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No interviewed candidates found for final selection',
      selected: [],
      rejected: []
    }
  }];
}

// Sort by combined score (descending)
interviewedCandidates.sort((a, b) => b.combined_score - a.combined_score);

// Select top 20 candidates
const selectedCandidates = interviewedCandidates.slice(0, 20);
const rejectedCandidates = interviewedCandidates.slice(20);

// Update candidate statuses
interviewedCandidates.forEach((candidate, index) => {
  const candidateIndex = candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    if (index < 20) {
      candidates[candidateIndex].status = 'final_selected';
      candidates[candidateIndex].final_selection_date = new Date().toISOString();
    } else {
      candidates[candidateIndex].status = 'interview_rejected';
      candidates[candidateIndex].interview_rejection_date = new Date().toISOString();
    }
  }
});

// Update candidates.json
fs.writeFileSync(candidatesPath, JSON.stringify(candidates, null, 2));

// Create selection summary
const selectedSummary = selectedCandidates.map(c => ({
  candidate_id: c.candidate_id,
  name: c.name,
  email: c.email,
  phone: c.phone,
  ats_score: c.ats_score,
  interview_score: c.interview_score,
  combined_score: c.combined_score
}));

// Generate ICS calendar file for final interviews
const icsContent = `BEGIN:VCALENDAR
VERSION:2.0
PRODID:-//HR System//Final Interviews//EN
${selectedCandidates.map((candidate, index) => {
  const interviewDate = new Date();
  interviewDate.setDate(interviewDate.getDate() + 7 + index); // Schedule over next week
  const startTime = interviewDate.toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z';
  const endTime = new Date(interviewDate.getTime() + 60*60*1000).toISOString().replace(/[-:]/g, '').split('.')[0] + 'Z';

  return `BEGIN:VEVENT
UID:final-interview-${candidate.candidate_id}
DTSTAMP:${startTime}
DTSTART:${startTime}
DTEND:${endTime}
SUMMARY:Final Interview - ${candidate.name}
DESCRIPTION:Final interview with ${candidate.name}\nCombined Score: ${candidate.combined_score}/100\nATS Score: ${candidate.ats_score}/100\nInterview Score: ${candidate.interview_score}/100
LOCATION:Conference Room / Video Call
END:VEVENT`;
}).join('\n')}
END:VCALENDAR`;

fs.writeFileSync('/tmp/final_interviews.ics', icsContent);

return [{
  json: {
    success: true,
    message: `Selected ${selectedCandidates.length} final candidates, rejected ${rejectedCandidates.length} after interview`,
    selected: selectedSummary,
    rejected_count: rejectedCandidates.length,
    total_processed: interviewedCandidates.length,
    calendar_file_created: true
  }
}];

// Send final selection emails with interview feedback
const fs = require('fs');

// Get selected candidates for this batch
const selected = $input.first().json.selected;

if (!selected || selected.length === 0) {
  return [];
}

// Load interview data for feedback
const interviewsPath = '/tmp/interviews.json';
let interviews = [];
try {
  interviews = JSON.parse(fs.readFileSync(interviewsPath, 'utf8'));
} catch (error) {
  interviews = [];
}

