// Daily cleanup and maintenance tasks
const fs = require('fs');
const path = require('path');

// Cleanup old files and logs
const cleanupResults = {
  logs_cleaned: 0,
  old_files_removed: 0,
  database_optimized: false,
  backup_created: false
};

// Rotate logs if they're too large (>10MB)
const logFiles = ['/tmp/submission_log.txt', '/tmp/interview_log.txt'];
logFiles.forEach(logFile => {
  if (fs.existsSync(logFile)) {
    const stats = fs.statSync(logFile);
    if (stats.size > 10 * 1024 * 1024) { // 10MB
      const timestamp = new Date().toISOString().split('T')[0];
      const backupName = logFile.replace('.txt', `_${timestamp}.txt`);
      fs.copyFileSync(logFile, backupName);
      fs.writeFileSync(logFile, `Log rotated on ${new Date().toISOString()}\n`);
      cleanupResults.logs_cleaned++;
    }
  }
});

// Clean up old resume files (older than 30 days)
const resumesDir = '/tmp/resumes';
if (fs.existsSync(resumesDir)) {
  const thirtyDaysAgo = new Date();
  thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

  const jobDirs = fs.readdirSync(resumesDir);
  jobDirs.forEach(jobDir => {
    const jobPath = path.join(resumesDir, jobDir);
    if (fs.statSync(jobPath).isDirectory()) {
      const jobStats = fs.statSync(jobPath);
      if (jobStats.mtime < thirtyDaysAgo) {
        // Remove old job directory
        fs.rmSync(jobPath, { recursive: true, force: true });
        cleanupResults.old_files_removed++;
      }
    }
  });
}

// Create database backups
const backupDir = '/tmp/backups';
if (!fs.existsSync(backupDir)) {
  fs.mkdirSync(backupDir);
}

const timestamp = new Date().toISOString().split('T')[0];
const dataFiles = [
  '/tmp/jobs.json',
  '/tmp/candidates.json', 
  '/tmp/interviews.json',
  '/tmp/interview_questions.json'
];

dataFiles.forEach(file => {
  if (fs.existsSync(file)) {
    const filename = path.basename(file, '.json');
    const backupFile = path.join(backupDir, `${filename}_${timestamp}.json`);
    if (!fs.existsSync(backupFile)) {
      fs.copyFileSync(file, backupFile);
      cleanupResults.backup_created = true;
    }
  }
});

// Optimize databases by removing duplicates and invalid entries
const candidatesPath = '/tmp/candidates.json';
if (fs.existsSync(candidatesPath)) {
  let candidates = JSON.parse(fs.readFileSync(candidatesPath, 'utf8'));
  const originalCount = candidates.length;

  // Remove duplicates based on email
  const seen = new Set();
  candidates = candidates.filter(candidate => {
    if (seen.has(candidate.email)) {
      return false;
    }
    seen.add(candidate.email);
    return true;
  });

  if (candidates.length < originalCount) {
    fs.writeFileSync(candidatesPath, JSON.stringify(candidates, null, 2));
    cleanupResults.database_optimized = true;
  }
}

// System health check
const healthCheck = {
  disk_usage: {
    tmp_size_mb: 0,
    available_space: true
  },
  file_integrity: {
    jobs_valid: fs.existsSync('/tmp/jobs.json'),
    candidates_valid: fs.existsSync('/tmp/candidates.json'),
    templates_valid: fs.existsSync('/tmp/templates')
  },
  timestamp: new Date().toISOString()
};

// Calculate /tmp directory size
try {
  const tmpStats = fs.statSync('/tmp');
  healthCheck.disk_usage.tmp_size_mb = Math.round(tmpStats.size / (1024 * 1024));
  healthCheck.disk_usage.available_space = tmpStats.size < 500 * 1024 * 1024; // Less than 500MB
} catch (error) {
  healthCheck.disk_usage.available_space = false;
}

// Log cleanup results
const cleanupLog = `${new Date().toISOString()} - Cleanup completed: ${JSON.stringify(cleanupResults)}\n`;
fs.appendFileSync('/tmp/cleanup_log.txt', cleanupLog);

return [{
  json: {
    success: true,
    message: 'Daily cleanup completed successfully',
    cleanup_results: cleanupResults,
    health_check: healthCheck,
    timestamp: new Date().toISOString()
  }
}];

