const batch = $('Enhanced Enrichment').first().json;
const config = batch.config;
const scoredLeads = [];
const scoringErrors = [];

const aiResponses = $input.all();

aiResponses.forEach((response, index) => {
  const lead = batch.leads[index];
  if (!lead) return;

  let aiAnalysis = {};
  try {
    const aiContent = response.json.choices?.[0]?.message?.content;
    if (aiContent) {
      aiAnalysis = JSON.parse(aiContent);
    } else {
      throw new Error('No AI response content');
    }

    if (typeof aiAnalysis.lead_score !== 'number' || aiAnalysis.lead_score < 1 || aiAnalysis.lead_score > 10) {
      throw new Error('Invalid lead_score in AI response');
    }

  } catch (error) {
    // Enhanced fallback scoring with more factors
    const baseScore = Math.max(1, Math.min(10, Math.floor((lead.enrichment_score / 10))));
    const activityBonus = (lead.recent_activity.linkedin_posts_last_month > 5) ? 1 : 0;
    const socialBonus = (lead.social_media_presence.linkedin_followers > 1000) ? 0.5 : 0;
    const techStackBonus = (lead.company_tech_stack && lead.company_tech_stack.length > 3) ? 0.5 : 0;

    aiAnalysis = {
      lead_score: Math.min(10, baseScore + activityBonus + socialBonus + techStackBonus),
      confidence_level: 'medium',
      fit_reason: 'Automated scoring based on enrichment data - manual review recommended',
      priority_level: lead.enrichment_score > 80 ? 'high' : 'medium',
      personalization_angle: `${lead.company_industry} expertise and ${lead.professional_interests[0]} focus`,
      pain_points: ['Scaling challenges', 'Technology efficiency', 'Team productivity'],
      timing_indicators: lead.company_recent_news ? ['Recent company expansion'] : ['General market timing'],
      conversation_starters: [`${lead.professional_interests[0]} trends`, 'Company growth strategies', 'Industry innovations'],
      objection_handling: ['ROI concerns', 'Implementation timeline', 'Budget constraints'],
      next_best_action: 'Send personalized LinkedIn connection request'
    };

    scoringErrors.push({
      lead_id: lead.lead_id,
      error: error.message,
      failed_at: new Date().toISOString()
    });
  }

  // Calculate enhanced final score with more factors
  const enrichmentWeight = 0.2;
  const aiScoreWeight = 0.5;
  const activityWeight = 0.2;
  const deliverabilityWeight = 0.1;

  const finalScore = Math.round(
    (lead.enrichment_score * enrichmentWeight +
     aiAnalysis.lead_score * 10 * aiScoreWeight +
     (lead.recent_activity.linkedin_posts_last_month * 2) * activityWeight +
     lead.email_deliverability_score * deliverabilityWeight) / 10 * 10
  ) / 10;

  const scoredLead = {
    ...lead,
    ...aiAnalysis,
    final_score: Math.min(10, finalScore),
    ai_scored_at: new Date().toISOString(),
    processing_status: 'ai_scored',
    scoring_model: config.ai_prompt_template,
    has_scoring_error: scoringErrors.some(err => err.lead_id === lead.lead_id),

    // NEW: Enhanced scoring metadata
    scoring_factors: {
      enrichment_contribution: lead.enrichment_score * enrichmentWeight,
      ai_contribution: aiAnalysis.lead_score * 10 * aiScoreWeight,
      activity_contribution: (lead.recent_activity.linkedin_posts_last_month * 2) * activityWeight,
      deliverability_contribution: lead.email_deliverability_score * deliverabilityWeight
    },

    recommended_outreach_channels: {
      email: lead.email_verified && lead.email_deliverability_score > 70,
      linkedin: lead.linkedin_url && lead.social_media_presence.linkedin_followers > 100,
      sms: lead.phone && config.enable_sms_outreach && ['urgent', 'high'].includes(aiAnalysis.priority_level)
    }
  };

  scoredLeads.push(scoredLead);
});

// Sort by final score descending
scoredLeads.sort((a, b) => b.final_score - a.final_score);

return [{
  json: {
    ...batch,
    leads: scoredLeads,
    scoring_errors: scoringErrors,
    average_score: scoredLeads.reduce((sum, lead) => sum + lead.final_score, 0) / scoredLeads.length,
    high_quality_count: scoredLeads.filter(lead => lead.final_score >= 7).length,
    urgent_priority_count: scoredLeads.filter(lead => lead.priority_level === 'urgent').length,
    scoring_completed_at: new Date().toISOString()
  }
}];

