const leads = $('Filter High Quality').all();
const config = $('Load Enhanced Config').first().json;

// Create nurturing sequences for each high-quality lead
const nurturingSequences = [];

leads.forEach(leadItem => {
  const lead = leadItem.json;
  const outreachMessage = $('Generate Multilingual Outreach').first()?.json;

  // Create follow-up sequence
  for (let i = 1; i <= config.nurturing_messages_count; i++) {
    const delayDays = config.nurturing_sequence_days * i;

    nurturingSequences.push({
      json: {
        lead_id: lead.lead_id,
        lead_name: `${lead.first_name} ${lead.last_name}`,
        lead_email: lead.email,
        company: lead.company_name,
        sequence_step: i,
        delay_days: delayDays,
        scheduled_date: new Date(Date.now() + (delayDays * 24 * 60 * 60 * 1000)).toISOString(),
        nurturing_type: i === 1 ? 'follow_up_1' : i === 2 ? 'follow_up_2' : 'follow_up_3',
        original_message: outreachMessage ? JSON.parse(outreachMessage.choices[0].message.content).message_body : null,
        lead_context: {
          final_score: lead.final_score,
          priority_level: lead.priority_level,
          personalization_angle: lead.personalization_angle,
          pain_points: lead.pain_points,
          conversation_starters: lead.conversation_starters,
          assigned_language: lead.assigned_language,
          professional_interests: lead.professional_interests
        },
        status: 'scheduled',
        created_at: new Date().toISOString(),
        workflow_run_id: lead.workflow_run_id
      }
    });
  }
});

return nurturingSequences;

