// Collect comprehensive workflow analytics
const allBatches = $('Process Enhanced Scoring').all();
const config = $('Load Enhanced Config').first().json;
const deliverabilityData = $('Monitor Deliverability').first().json;

// Aggregate lead data from all batches
const allLeads = [];
const allErrors = [];
let totalDuplicates = 0;

allBatches.forEach(batch => {
  if (batch.json.leads) {
    allLeads.push(...batch.json.leads);
  }
  if (batch.json.enrichment_errors) {
    allErrors.push(...batch.json.enrichment_errors);
  }
  if (batch.json.scoring_errors) {
    allErrors.push(...batch.json.scoring_errors);
  }
  totalDuplicates += batch.json.duplicates_found || 0;
});

// Calculate comprehensive analytics
const analytics = {
  // Workflow Metadata
  workflow_run_id: config.workflow_run_id,
  triggered_by: config.triggered_by,
  config_template: config.ai_prompt_template,
  outreach_tone: config.outreach_tone,
  outreach_language: config.outreach_language,

  // Processing Statistics
  total_leads_discovered: allLeads.length,
  duplicates_filtered: totalDuplicates,
  unique_leads_processed: allLeads.length - totalDuplicates,
  successfully_enriched: allLeads.filter(l => l.processing_status === 'enriched' || l.processing_status === 'ai_scored').length,
  successfully_scored: allLeads.filter(l => l.ai_scored_at).length,

  // Quality Metrics
  average_final_score: allLeads.reduce((sum, lead) => sum + (lead.final_score || 0), 0) / allLeads.length || 0,
  average_enrichment_score: allLeads.reduce((sum, lead) => sum + (lead.enrichment_score || 0), 0) / allLeads.length || 0,
  average_deliverability_score: allLeads.reduce((sum, lead) => sum + (lead.email_deliverability_score || 0), 0) / allLeads.length || 0,

  // Enhanced Score Distribution
  score_distribution: {
    urgent: allLeads.filter(lead => lead.priority_level === 'urgent').length,
    high: allLeads.filter(lead => (lead.final_score || 0) >= 7).length,
    medium: allLeads.filter(lead => (lead.final_score || 0) >= 4 && (lead.final_score || 0) < 7).length,
    low: allLeads.filter(lead => (lead.final_score || 0) < 4).length
  },

  // Confidence Distribution
  confidence_distribution: {
    high: allLeads.filter(lead => lead.confidence_level === 'high').length,
    medium: allLeads.filter(lead => lead.confidence_level === 'medium').length,
    low: allLeads.filter(lead => lead.confidence_level === 'low').length
  },

  // Geographic Distribution
  geographic_distribution: {
    countries: [...new Set(allLeads.map(lead => lead.country).filter(Boolean))],
    top_countries: Object.entries(
      allLeads.reduce((acc, lead) => {
        if (lead.country) acc[lead.country] = (acc[lead.country] || 0) + 1;
        return acc;
      }, {})
    ).sort(([,a], [,b]) => b - a).slice(0, 5)
  },

  // Industry Analysis
  industry_analysis: {
    industries_covered: [...new Set(allLeads.map(lead => lead.company_industry).filter(Boolean))],
    top_industries: Object.entries(
      allLeads.reduce((acc, lead) => {
        if (lead.company_industry) acc[lead.company_industry] = (acc[lead.company_industry] || 0) + 1;
        return acc;
      }, {})
    ).sort(([,a], [,b]) => b - a).slice(0, 5)
  },

  // Company Analysis
  company_analysis: {
    top_companies: [...new Set(allLeads.map(lead => lead.company_name))].slice(0, 10),
    company_size_distribution: {
      startup: allLeads.filter(lead => lead.company_size < 50).length,
      small: allLeads.filter(lead => lead.company_size >= 50 && lead.company_size < 200).length,
      medium: allLeads.filter(lead => lead.company_size >= 200 && lead.company_size < 1000).length,
      large: allLeads.filter(lead => lead.company_size >= 1000).length
    },
    funding_stages: [...new Set(allLeads.map(lead => lead.company_funding_stage).filter(Boolean))]
  },

  // Sales Rep Performance
  sales_rep_performance: config.enable_rep_assignment ? 
    config.sales_reps.map(rep => ({
      rep_id: rep.id,
      rep_name: rep.name,
      leads_assigned: allLeads.filter(lead => lead.assigned_sales_rep?.id === rep.id).length,
      avg_lead_score: allLeads
        .filter(lead => lead.assigned_sales_rep?.id === rep.id)
        .reduce((sum, lead) => sum + (lead.final_score || 0), 0) / 
        allLeads.filter(lead => lead.assigned_sales_rep?.id === rep.id).length || 0,
      territories: rep.territories,
      industries: rep.industries
    })) : [],

  // Multi-Channel Analysis
  channel_analysis: {
    email_eligible: allLeads.filter(lead => lead.recommended_outreach_channels?.email).length,
    linkedin_eligible: allLeads.filter(lead => lead.recommended_outreach_channels?.linkedin).length,
    sms_eligible: allLeads.filter(lead => lead.recommended_outreach_channels?.sms).length,
    multi_channel_leads: allLeads.filter(lead => {
      const channels = lead.recommended_outreach_channels || {};
      return [channels.email, channels.linkedin, channels.sms].filter(Boolean).length > 1;
    }).length
  },

  // Language Distribution
  language_distribution: Object.entries(
    allLeads.reduce((acc, lead) => {
      if (lead.assigned_language) acc[lead.assigned_language] = (acc[lead.assigned_language] || 0) + 1;
      return acc;
    }, {})
  ).sort(([,a], [,b]) => b - a),

  // Feature Usage Statistics
  feature_usage: {
    lead_nurturing_enabled: config.enable_lead_nurturing,
    linkedin_automation_enabled: config.enable_linkedin_automation,
    sms_outreach_enabled: config.enable_sms_outreach,
    zapier_sync_enabled: config.enable_zapier_sync,
    airtable_sync_enabled: config.enable_airtable_sync,
    rep_assignment_enabled: config.enable_rep_assignment,
    reply_engine_enabled: config.enable_reply_engine,
    deliverability_monitoring_enabled: config.enable_deliverability_monitor
  },

  // Error Analysis
  error_analysis: {
    total_errors: allErrors.length,
    error_rate: (allErrors.length / (allLeads.length || 1)) * 100,
    error_types: allErrors.reduce((acc, error) => {
      const type = error.type || 'unknown';
      acc[type] = (acc[type] || 0) + 1;
      return acc;
    }, {})
  },

  // Performance Metrics
  performance_metrics: {
    batch_count: allBatches.length,
    average_batch_size: allBatches.reduce((sum, batch) => sum + batch.json.batch_size, 0) / allBatches.length,
    processing_time_minutes: (Date.now() - new Date(config.config_loaded_at).getTime()) / 60000,
    leads_per_minute: allLeads.length / ((Date.now() - new Date(config.config_loaded_at).getTime()) / 60000)
  },

  // Deliverability Integration
  deliverability_metrics: {
    quota_usage: deliverabilityData.quota_percentage_used,
    bounce_rate: deliverabilityData.bounce_rate,
    spam_rate: deliverabilityData.spam_rate,
    open_rate: deliverabilityData.open_rate,
    response_rate: deliverabilityData.response_rate,
    warmup_score: deliverabilityData.warmup_score,
    alerts_count: deliverabilityData.alerts.length,
    reputation_status: deliverabilityData.sender_reputation
  },

  // Recommendations Engine
  recommendations: [],

  // Timing Metadata
  workflow_started_at: config.config_loaded_at,
  workflow_completed_at: new Date().toISOString(),
  debug_mode: config.debug_mode,
  test_mode: config.test_mode
};

// Generate intelligent recommendations
if (analytics.score_distribution.high < analytics.total_leads_discovered * 0.2) {
  analytics.recommendations.push('Consider refining ICP criteria - low percentage of high-quality leads (${(analytics.score_distribution.high / analytics.total_leads_discovered * 100).toFixed(1)}%)');
}

if (analytics.error_analysis.error_rate > 10) {
  analytics.recommendations.push('High error rate detected (${analytics.error_analysis.error_rate.toFixed(1)}%) - review API credentials and rate limits');
}

if (analytics.confidence_distribution.low > analytics.total_leads_discovered * 0.3) {
  analytics.recommendations.push('Many low-confidence scores (${(analytics.confidence_distribution.low / analytics.total_leads_discovered * 100).toFixed(1)}%) - improve data sources');
}

if (parseFloat(analytics.deliverability_metrics.quota_usage) > 85) {
  analytics.recommendations.push('High email quota usage (${analytics.deliverability_metrics.quota_usage}%) - consider upgrading or spreading sends');
}

if (parseFloat(analytics.deliverability_metrics.bounce_rate) > 3) {
  analytics.recommendations.push('High bounce rate (${analytics.deliverability_metrics.bounce_rate}%) - improve list quality');
}

if (analytics.channel_analysis.multi_channel_leads > 0) {
  analytics.recommendations.push('${analytics.channel_analysis.multi_channel_leads} leads eligible for multi-channel outreach - leverage for higher response rates');
}

if (analytics.sales_rep_performance.length > 0) {
  const imbalancedReps = analytics.sales_rep_performance.filter(rep => 
    rep.leads_assigned > analytics.total_leads_discovered / analytics.sales_rep_performance.length * 1.5
  );
  if (imbalancedReps.length > 0) {
    analytics.recommendations.push('Lead distribution imbalance detected - review territory assignments');
  }
}

return [{ json: analytics }];
