const items = [];
const input = $input.first().json;

if (input.people && Array.isArray(input.people)) {
  for (const person of input.people) {
    const leadData = {
      id: person.id,
      first_name: person.first_name,
      last_name: person.last_name,
      title: person.title,
      email: person.email,
      linkedin_url: person.linkedin_url,
      company_name: person.organization?.name || '',
      company_domain: person.organization?.primary_domain || '',
      company_industry: person.organization?.industry || '',
      company_size: person.organization?.estimated_num_employees || 0,
      company_description: person.organization?.short_description || '',
      location: person.city + ', ' + person.state + ', ' + person.country,
      phone: person.phone_numbers?.[0]?.sanitized_number || '',
      source: 'Apollo.io',
      discovered_at: new Date().toISOString()
    };

    items.push({ json: leadData });
  }
}

return items;

const apolloData = $('Process Apollo Data').first().json;
const hunterData = $('Hunter Email Finder').first().json;

const enrichedLead = {
  ...apolloData,
  email_verified: hunterData.data?.email || apolloData.email,
  email_confidence: hunterData.data?.confidence || null,
  email_score: hunterData.data?.score || null,
  enrichment_score: (
    (apolloData.email ? 20 : 0) +
    (hunterData.data?.email ? 25 : 0) +
    (apolloData.linkedin_url ? 15 : 0) +
    (apolloData.phone ? 10 : 0) + 30
  ),
  enriched_at: new Date().toISOString()
};

return [{ json: enrichedLead }];

