const leadData = $('Merge Enriched Data').first().json;
const aiResponse = $input.first().json;

let aiAnalysis = {};
try {
  aiAnalysis = JSON.parse(aiResponse.choices[0].message.content);
} catch (e) {
  console.error('Failed to parse AI response:', e);
  aiAnalysis = {
    lead_score: 5,
    fit_reason: 'Unable to analyze - manual review required',
    priority_level: 'medium',
    personalization_angle: 'General industry expertise',
    pain_points: ['Scaling challenges', 'Tech efficiency'],
    conversation_starters: ['Industry expertise', 'Company growth']
  };
}

const scoredLead = {
  ...leadData,
  ...aiAnalysis,
  ai_scored_at: new Date().toISOString(),
  final_score: Math.round((leadData.enrichment_score * 0.3 + aiAnalysis.lead_score * 10 * 0.7) / 10 * 10) / 10
};

return [{ json: scoredLead }];

