const allLeads = $('Process AI Scoring').all();
const highQualityLeads = $('Filter High Quality Leads').all();

const summary = {
  total_leads_found: allLeads.length,
  high_quality_leads: highQualityLeads.length,
  average_score: allLeads.reduce((sum, lead) => sum + (lead.json.final_score || 0), 0) / allLeads.length || 0,
  score_distribution: {
    high: allLeads.filter(lead => (lead.json.final_score || 0) >= 7).length,
    medium: allLeads.filter(lead => (lead.json.final_score || 0) >= 4 && (lead.json.final_score || 0) < 7).length,
    low: allLeads.filter(lead => (lead.json.final_score || 0) < 4).length
  },
  companies_analyzed: [...new Set(allLeads.map(lead => lead.json.company_name))],
  industries: [...new Set(allLeads.map(lead => lead.json.company_industry).filter(Boolean))],
  workflow_completed_at: new Date().toISOString()
};

return [{ json: summary }];
