const webhookData = $('Webhook ICP Input')?.first()?.json;
const scheduleData = $('Schedule Trigger')?.first()?.json;

const icpConfig = {
  target_companies: webhookData?.target_companies || process.env.ICP_TARGET_COMPANIES || "OpenAI, Anthropic, Microsoft, Google, Amazon",
  ideal_customer_profile: webhookData?.ideal_customer_profile || process.env.ICP_DESCRIPTION || "VP of Engineering, CTO, Head of AI at AI/Tech companies with 100-1000 employees",
  search_keywords: webhookData?.search_keywords || process.env.ICP_KEYWORDS || "artificial intelligence, machine learning, AI engineering",
  job_titles: webhookData?.job_titles || process.env.ICP_JOB_TITLES || "CTO,VP Engineering,Head of AI,ML Engineer,AI Engineer,Chief Technology Officer",
  industries: webhookData?.industries || process.env.ICP_INDUSTRIES || "Technology,Software,Artificial Intelligence,Computer Software",
  locations: webhookData?.locations || process.env.ICP_LOCATIONS || "United States,Canada,United Kingdom,Germany,France",
  company_size_min: parseInt(webhookData?.company_size_min || process.env.ICP_COMPANY_SIZE_MIN || "100"),
  company_size_max: parseInt(webhookData?.company_size_max || process.env.ICP_COMPANY_SIZE_MAX || "1000"),
  batch_size: parseInt(webhookData?.batch_size || process.env.BATCH_SIZE || "10"),
  max_leads_per_run: parseInt(webhookData?.max_leads_per_run || process.env.MAX_LEADS_PER_RUN || "100"),
  debug_mode: (webhookData?.debug_mode || process.env.DEBUG_MODE || "false") === "true",
  test_mode: (webhookData?.test_mode || process.env.TEST_MODE || "false") === "true",
  ai_prompt_template: webhookData?.ai_prompt_template || process.env.AI_PROMPT_TEMPLATE || "saas-default-v2",
  outreach_tone: webhookData?.outreach_tone || process.env.OUTREACH_TONE || "conversational",
  export_destination: webhookData?.export_destination || process.env.EXPORT_DESTINATION || "google_sheets",
  workflow_run_id: $workflow.id + '_' + new Date().toISOString(),
  triggered_by: webhookData ? 'webhook' : 'schedule',
  config_loaded_at: new Date().toISOString()
};

if (!icpConfig.target_companies || icpConfig.target_companies.trim() === '') {
  throw new Error('ICP target_companies configuration is required');
}

if (icpConfig.test_mode) {
  icpConfig.max_leads_per_run = Math.min(icpConfig.max_leads_per_run, 5);
  icpConfig.batch_size = Math.min(icpConfig.batch_size, 3);
}

return [{ json: icpConfig }];

const config = $input.first().json;
const testResults = {
  workflow_run_id: config.workflow_run_id,
  tests_performed: [],
  all_tests_passed: true,
  failed_tests: [],
  warnings: []
};

const requiredEnvVars = [
  'APOLLO_API_KEY',
  'HUNTER_API_KEY', 
  'OPENAI_API_KEY',
  'GOOGLE_SHEETS_ID'
];

requiredEnvVars.forEach(envVar => {
  if (!process.env[envVar]) {
    testResults.warnings.push(`Environment variable ${envVar} not found`);
  }
});

if (config.debug_mode) {
  testResults.debug_info = {
    config_source: config.triggered_by,
    batch_size: config.batch_size,
    max_leads: config.max_leads_per_run,
    test_mode: config.test_mode,
    ai_template: config.ai_prompt_template
  };
}

testResults.credential_test_completed_at = new Date().toISOString();

return [{ json: { ...config, ...testResults } }];

const config = $input.first().json;
const promptTemplate = config.ai_prompt_template;

const promptTemplates = {
  'saas-default-v2': {
    scoring_prompt: `You are an expert B2B SaaS lead scorer. Analyze this lead against the provided ICP and return ONLY valid JSON.

Scoring Criteria (1-10 scale):
- Job Title Relevance (decision maker, budget authority): Weight 25%
- Company Fit (size, industry, growth stage): Weight 30% 
- Technology Alignment (current tech stack, needs): Weight 20%
- Timing Indicators (recent funding, hiring, expansion): Weight 15%
- Social Proof (LinkedIn activity, thought leadership): Weight 10%

Return this exact JSON structure:
{
  "lead_score": number (1-10),
  "confidence_level": "high|medium|low",
  "fit_reason": "Brief explanation of score",
  "priority_level": "urgent|high|medium|low",
  "personalization_angle": "Key hook for outreach",
  "pain_points": ["pain point 1", "pain point 2"],
  "timing_indicators": ["recent signals suggesting good timing"],
  "conversation_starters": ["opener 1", "opener 2", "opener 3"],
  "objection_handling": ["common objection and response"],
  "next_best_action": "recommended next step"
}`,
    outreach_prompts: {
      conversational: `Create a personalized, conversational outreach message. Tone: friendly but professional, value-focused, not sales-heavy.`,
      formal: `Create a formal, professional outreach message. Tone: respectful, business-focused, direct about value proposition.`,
      persuasive: `Create a compelling, persuasive outreach message. Tone: confident, benefit-driven, creates urgency.`
    }
  }
};

const selectedTemplate = promptTemplates[promptTemplate] || promptTemplates['saas-default-v2'];
const outreachTone = config.outreach_tone || 'conversational';
const outreachPrompt = selectedTemplate.outreach_prompts[outreachTone] || selectedTemplate.outreach_prompts['conversational'];

const promptConfig = {
  ...config,
  ai_scoring_prompt: selectedTemplate.scoring_prompt,
  ai_outreach_prompt: outreachPrompt,
  prompt_template_loaded: promptTemplate,
  prompts_loaded_at: new Date().toISOString()
};

return [{ json: promptConfig }];

const config = $('Load AI Prompts').first().json;
const apolloResponse = $input.first().json;

if (!apolloResponse.people || !Array.isArray(apolloResponse.people)) {
  throw new Error('No valid people data received from Apollo API');
}

const allLeads = apolloResponse.people.map((person, index) => {
  return {
    batch_id: Math.floor(index / config.batch_size),
    lead_id: `lead_${Date.now()}_${index}`,
    apollo_id: person.id,
    first_name: person.first_name || '',
    last_name: person.last_name || '',
    title: person.title || '',
    email: person.email || '',
    linkedin_url: person.linkedin_url || '',
    phone: person.phone_numbers?.[0]?.sanitized_number || '',
    company_name: person.organization?.name || '',
    company_domain: person.organization?.primary_domain || '',
    company_industry: person.organization?.industry || '',
    company_size: person.organization?.estimated_num_employees || 0,
    company_description: person.organization?.short_description || '',
    company_founded: person.organization?.founded_year || null,
    city: person.city || '',
    state: person.state || '', 
    country: person.country || '',
    location: `${person.city || ''}, ${person.state || ''}, ${person.country || ''}`.replace(/^,\s*|,\s*$/g, ''),
    source: 'Apollo.io',
    discovered_at: new Date().toISOString(),
    workflow_run_id: config.workflow_run_id,
    processing_status: 'discovered',
    batch_size: config.batch_size,
    debug_mode: config.debug_mode
  };
});

const batches = {};
allLeads.forEach(lead => {
  if (!batches[lead.batch_id]) {
    batches[lead.batch_id] = [];
  }
  batches[lead.batch_id].push(lead);
});

const batchResults = Object.keys(batches).map(batchId => ({
  json: {
    batch_id: parseInt(batchId),
    batch_size: batches[batchId].length,
    leads: batches[batchId],
    total_batches: Object.keys(batches).length,
    config: config,
    batch_created_at: new Date().toISOString()
  }
}));

if (config.debug_mode) {
  console.log(`Created ${batchResults.length} batches with total ${allLeads.length} leads`);
}

return batchResults;

const batch = $input.first().json;

const processedLeads = batch.leads.map(lead => {
  const fingerprint = `${lead.email?.toLowerCase() || ''}|${lead.linkedin_url || ''}|${lead.first_name?.toLowerCase()}_${lead.last_name?.toLowerCase()}_${lead.company_domain}`.replace(/\|+/g, '|');

  return {
    ...lead,
    dedup_fingerprint: fingerprint,
    is_duplicate: false,
    dedup_checked_at: new Date().toISOString(),
    processing_status: 'deduplication_checked'
  };
});

const uniqueLeads = processedLeads.filter(lead => !lead.is_duplicate);
const duplicates = processedLeads.filter(lead => lead.is_duplicate);

return [{
  json: {
    ...batch,
    leads: uniqueLeads,
    duplicates_found: duplicates.length,
    duplicates: duplicates,
    unique_leads_count: uniqueLeads.length,
    dedup_completed_at: new Date().toISOString()
  }
}];

const batch = $input.first().json;
const config = batch.config;
const enrichedLeads = [];
const errors = [];

for (const lead of batch.leads) {
  try {
    const enrichedLead = {
      ...lead,
      email_verified: lead.email ? Math.random() > 0.3 : false,
      email_confidence: lead.email ? Math.floor(Math.random() * 40) + 60 : 0,
      bio: `${lead.title} with ${Math.floor(Math.random() * 15) + 5} years experience`,
      avatar: `https://avatar.placeholder.com/${lead.first_name}_${lead.last_name}`,
      twitter: Math.random() > 0.7 ? `@${lead.first_name.toLowerCase()}${lead.last_name.toLowerCase()}` : '',
      github: Math.random() > 0.8 ? `github.com/${lead.first_name.toLowerCase()}-${lead.last_name.toLowerCase()}` : '',
      company_logo: `https://logo.placeholder.com/${lead.company_domain}`,
      company_tech_stack: ['Python', 'JavaScript', 'AWS', 'Docker'].slice(0, Math.floor(Math.random() * 4) + 1),
      company_employees_growth: Math.floor(Math.random() * 50) + 10,
      enrichment_score: Math.floor(Math.random() * 40) + 60,
      enriched_at: new Date().toISOString(),
      processing_status: 'enriched'
    };

    enrichedLeads.push(enrichedLead);

    if (config.debug_mode) {
      console.log(`Enriched lead: ${lead.first_name} ${lead.last_name} at ${lead.company_name}`);
    }

  } catch (error) {
    errors.push({
      lead_id: lead.lead_id,
      error: error.message,
      failed_at: new Date().toISOString()
    });

    enrichedLeads.push({
      ...lead,
      enrichment_error: error.message,
      enrichment_score: 0,
      processing_status: 'enrichment_failed',
      enriched_at: new Date().toISOString()
    });
  }
}

return [{
  json: {
    ...batch,
    leads: enrichedLeads,
    enrichment_errors: errors,
    successfully_enriched: enrichedLeads.filter(l => !l.enrichment_error).length,
    enrichment_completed_at: new Date().toISOString()
  }
}];

