const allBatches = $('Process AI Scoring').all();
const config = $('Load AI Prompts').first().json;

const allLeads = [];
const allErrors = [];
let totalDuplicates = 0;

allBatches.forEach(batch => {
  if (batch.json.leads) {
    allLeads.push(...batch.json.leads);
  }
  if (batch.json.enrichment_errors) {
    allErrors.push(...batch.json.enrichment_errors);
  }
  if (batch.json.scoring_errors) {
    allErrors.push(...batch.json.scoring_errors);
  }
  totalDuplicates += batch.json.duplicates_found || 0;
});

const summary = {
  workflow_run_id: config.workflow_run_id,
  triggered_by: config.triggered_by,
  config_template: config.ai_prompt_template,
  outreach_tone: config.outreach_tone,
  export_destination: config.export_destination,
  total_leads_discovered: allLeads.length,
  duplicates_filtered: totalDuplicates,
  unique_leads_processed: allLeads.length - totalDuplicates,
  successfully_enriched: allLeads.filter(l => l.processing_status === 'enriched' || l.processing_status === 'ai_scored').length,
  successfully_scored: allLeads.filter(l => l.ai_scored_at).length,
  average_final_score: allLeads.reduce((sum, lead) => sum + (lead.final_score || 0), 0) / allLeads.length || 0,
  average_enrichment_score: allLeads.reduce((sum, lead) => sum + (lead.enrichment_score || 0), 0) / allLeads.length || 0,
  score_distribution: {
    urgent: allLeads.filter(lead => lead.priority_level === 'urgent').length,
    high: allLeads.filter(lead => (lead.final_score || 0) >= 7).length,
    medium: allLeads.filter(lead => (lead.final_score || 0) >= 4 && (lead.final_score || 0) < 7).length,
    low: allLeads.filter(lead => (lead.final_score || 0) < 4).length
  },
  top_companies: [...new Set(allLeads.map(lead => lead.company_name))].slice(0, 10),
  top_titles: [...new Set(allLeads.map(lead => lead.title))].slice(0, 10),
  industries_covered: [...new Set(allLeads.map(lead => lead.company_industry).filter(Boolean))],
  locations_covered: [...new Set(allLeads.map(lead => lead.location).filter(Boolean))].slice(0, 10),
  confidence_distribution: {
    high: allLeads.filter(lead => lead.confidence_level === 'high').length,
    medium: allLeads.filter(lead => lead.confidence_level === 'medium').length,
    low: allLeads.filter(lead => lead.confidence_level === 'low').length
  },
  total_errors: allErrors.length,
  error_rate: (allErrors.length / (allLeads.length || 1)) * 100,
  batch_count: allBatches.length,
  average_batch_size: allBatches.reduce((sum, batch) => sum + batch.json.batch_size, 0) / allBatches.length,
  processing_time_minutes: (Date.now() - new Date(config.config_loaded_at).getTime()) / 60000,
  workflow_started_at: config.config_loaded_at,
  workflow_completed_at: new Date().toISOString(),
  debug_mode: config.debug_mode,
  test_mode: config.test_mode
};

summary.recommendations = [];
if (summary.score_distribution.high < summary.total_leads_discovered * 0.2) {
  summary.recommendations.push('Consider refining ICP criteria - low percentage of high-quality leads');
}
if (summary.error_rate > 10) {
  summary.recommendations.push('High error rate detected - check API credentials and rate limits');
}

return [{ json: summary }];

