// Process AI scoring results and combine with lead data
const batch = $('Batch Enrichment').first().json;
const config = batch.config;
const scoredLeads = [];
const scoringErrors = [];

// Process each AI response
const aiResponses = $input.all();

aiResponses.forEach((response, index) => {
  const lead = batch.leads[index];
  if (!lead) return;

  let aiAnalysis = {};
  try {
    // Parse AI response
    const aiContent = response.json.choices?.[0]?.message?.content;
    if (aiContent) {
      aiAnalysis = JSON.parse(aiContent);
    } else {
      throw new Error('No AI response content');
    }

    // Validate required fields
    if (typeof aiAnalysis.lead_score !== 'number' || aiAnalysis.lead_score < 1 || aiAnalysis.lead_score > 10) {
      throw new Error('Invalid lead_score in AI response');
    }

  } catch (error) {
    // Fallback scoring
    aiAnalysis = {
      lead_score: Math.max(1, Math.min(10, Math.floor((lead.enrichment_score / 10) + Math.random() * 3))),
      confidence_level: 'low',
      fit_reason: 'AI parsing failed - manual review required',
      priority_level: 'medium',
      personalization_angle: 'Industry expertise and company growth',
      pain_points: ['Scaling challenges', 'Technology efficiency'],
      timing_indicators: ['General market timing'],
      conversation_starters: ['Industry trends', 'Company growth'],
      objection_handling: ['ROI concerns'],
      next_best_action: 'Schedule discovery call'
    };

    scoringErrors.push({
      lead_id: lead.lead_id,
      error: error.message,
      failed_at: new Date().toISOString()
    });
  }

  // Calculate final composite score
  const finalScore = Math.round(
    (lead.enrichment_score * 0.3 + aiAnalysis.lead_score * 10 * 0.7) / 10 * 10
  ) / 10;

  const scoredLead = {
    ...lead,
    ...aiAnalysis,
    final_score: finalScore,
    ai_scored_at: new Date().toISOString(),
    processing_status: 'ai_scored',
    scoring_model: config.ai_prompt_template,
    has_scoring_error: scoringErrors.some(err => err.lead_id === lead.lead_id)
  };

  scoredLeads.push(scoredLead);
});

// Sort by final score descending
scoredLeads.sort((a, b) => b.final_score - a.final_score);

return [{
  json: {
    ...batch,
    leads: scoredLeads,
    scoring_errors: scoringErrors,
    average_score: scoredLeads.reduce((sum, lead) => sum + lead.final_score, 0) / scoredLeads.length,
    high_quality_count: scoredLeads.filter(lead => lead.final_score >= 7).length,
    scoring_completed_at: new Date().toISOString()
  }
}];

