const workflow = this.getWorkflow();
const staticData = workflow.staticData;

if (!staticData.jobs) {
    staticData.jobs = [];
}
if (!staticData.candidates) {
    staticData.candidates = [];
}
if (!staticData.interviews) {
    staticData.interviews = [];
}

const jobData = {
    job_id: `job_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    company_name: $input.first().json.data['Company Name'],
    job_title: $input.first().json.data['Job Title'],
    job_description: $input.first().json.data['Job Description'],
    limit_type: $input.first().json.data['Resume Collection Limit Type'],
    limit_value: $input.first().json.data['Resume Collection Limit Type'] === 'Number Limit' ? 
        ($input.first().json.data['Resume Limit Number'] || 1000) : 
        ($input.first().json.data['Time Limit Days'] || 7),
    status: 'active',
    created_date: new Date().toISOString(),
    resume_count: 0,
    candidates: []
};

staticData.jobs.push(jobData);

return [{
    json: {
        success: true,
        job_id: jobData.job_id,
        message: 'Job details stored successfully',
        jobData: jobData
    }
}];

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const submissionData = $input.first().json;
const jobId = submissionData.job_id;

if (!jobId) {
  return [{
    json: {
      success: false,
      error: 'Job ID is required'
    }
  }];
}

if (!staticData.jobs) {
  return [{
    json: {
      success: false,
      error: 'Jobs database not found'
    }
  }];
}

const job = staticData.jobs.find(j => j.job_id === jobId);
if (!job) {
  return [{
    json: {
      success: false,
      error: 'Job not found'
    }
  }];
}

if (job.status !== 'active') {
  return [{
    json: {
      success: false,
      error: 'Job is no longer accepting applications'
    }
  }];
}

let limitReached = false;

if (job.limit_type === 'Number Limit') {
  if (job.resume_count >= job.limit_value) {
    limitReached = true;
  }
} else if (job.limit_type === 'Time Limit') {
  const createdDate = new Date(job.created_date);
  const currentDate = new Date();
  const daysDiff = Math.ceil((currentDate - createdDate) / (1000 * 60 * 60 * 24));

  if (daysDiff > job.limit_value) {
    limitReached = true;
  }
}

if (limitReached) {
  const jobIndex = staticData.jobs.findIndex(j => j.job_id === jobId);
  if (jobIndex !== -1) {
    staticData.jobs[jobIndex].status = 'closed';
  }

  return [{
    json: {
      success: false,
      error: 'Application deadline has passed or maximum applications reached'
    }
  }];
}

return [{
  json: {
    success: true,
    job_id: jobId,
    job: job,
    submissionData: submissionData
  }
}];

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const data = $input.first().json;
const jobId = data.job_id;
const submissionData = data.submissionData;

const candidateId = `candidate_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
const candidate = {
  candidate_id: candidateId,
  job_id: jobId,
  name: submissionData.name,
  email: submissionData.email,
  phone: submissionData.phone,
  resume_text: submissionData.resume_text || '',
  submission_date: new Date().toISOString(),
  ats_score: 0,
  interview_score: 0,
  combined_score: 0,
  status: 'submitted'
};

if (!staticData.candidates) {
    staticData.candidates = [];
}

staticData.candidates.push(candidate);

const jobIndex = staticData.jobs.findIndex(j => j.job_id === jobId);
if (jobIndex !== -1) {
  staticData.jobs[jobIndex].resume_count += 1;
  staticData.jobs[jobIndex].candidates.push(candidateId);
}

return [{
  json: {
    success: true,
    message: 'Resume submitted successfully',
    candidate_id: candidateId,
    candidate: candidate
  }
}];

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

if (!staticData.candidates || staticData.candidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No candidates database found',
      candidates: []
    }
  }];
}

const pendingCandidates = staticData.candidates.filter(c => 
  c.status === 'submitted' && c.ats_score === 0
);

if (pendingCandidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No pending candidates for ATS scoring',
      candidates: []
    }
  }];
}

return pendingCandidates.slice(0, 10).map(candidate => ({ json: { candidate } }));

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const candidateData = $input.first().json.candidate;
const groqResponse = $input.first().json.choices[0].message.content;

let analysis = {};
try {
  analysis = JSON.parse(groqResponse);
} catch (error) {
  analysis = {
    skills: [],
    experience_years: 0,
    education_level: 'Unknown',
    keywords_found: [],
    technical_skills: [],
    soft_skills: [],
    certifications: []
  };
}

let score = 0;

const skillsScore = Math.min(analysis.skills.length * 2, 30);
score += skillsScore;

const expYears = analysis.experience_years || 0;
let experienceScore = 0;
if (expYears >= 5) experienceScore = 30;
else if (expYears >= 3) experienceScore = 25;
else if (expYears >= 1) experienceScore = 15;
else experienceScore = 5;
score += experienceScore;

const education = analysis.education_level.toLowerCase();
let educationScore = 0;
if (education.includes('master') || education.includes('mba')) educationScore = 20;
else if (education.includes('bachelor') || education.includes('degree')) educationScore = 15;
else if (education.includes('diploma') || education.includes('certificate')) educationScore = 10;
else educationScore = 5;
score += educationScore;

const keywordsScore = Math.min(analysis.keywords_found.length * 2, 20);
score += keywordsScore;

if (analysis.certifications && analysis.certifications.length > 0) {
  score += Math.min(analysis.certifications.length * 2, 10);
}

score = Math.min(Math.max(score, 0), 100);

const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidateData.candidate_id);
if (candidateIndex !== -1) {
  staticData.candidates[candidateIndex].ats_score = score;
  staticData.candidates[candidateIndex].ats_analysis = analysis;
  staticData.candidates[candidateIndex].status = 'ats_scored';
  staticData.candidates[candidateIndex].ats_scored_date = new Date().toISOString();
}

return [{
  json: {
    success: true,
    candidate_id: candidateData.candidate_id,
    ats_score: score,
    analysis: analysis,
    candidate: staticData.candidates[candidateIndex]
  }
}];

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

if (!staticData.candidates || staticData.candidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No candidates database found',
      shortlisted: [],
      rejected: []
    }
  }];
}

const scoredCandidates = staticData.candidates.filter(c => c.ats_score > 0 && c.status === 'ats_scored');

if (scoredCandidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No scored candidates found',
      shortlisted: [],
      rejected: []
    }
  }];
}

scoredCandidates.sort((a, b) => b.ats_score - a.ats_score);

const shortlisted = scoredCandidates.slice(0, 50);
const rejected = scoredCandidates.slice(50);

scoredCandidates.forEach((candidate, index) => {
  const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    if (index < 50) {
      staticData.candidates[candidateIndex].status = 'shortlisted';
      staticData.candidates[candidateIndex].shortlisted_date = new Date().toISOString();
    } else {
      staticData.candidates[candidateIndex].status = 'ats_rejected';
      staticData.candidates[candidateIndex].ats_rejected_date = new Date().toISOString();
    }
  }
});

const shortlistSummary = shortlisted.map(c => ({
  candidate_id: c.candidate_id,
  name: c.name,
  email: c.email,
  ats_score: c.ats_score,
  job_id: c.job_id
}));

return [{
  json: {
    success: true,
    message: `Shortlisted ${shortlisted.length} candidates, rejected ${rejected.length}`,
    shortlisted: shortlistSummary,
    rejected_count: rejected.length,
    total_processed: scoredCandidates.length
  }
}];

const shortlisted = $input.first().json.shortlisted;

if (!shortlisted || shortlisted.length === 0) {
  return [];
}

const emailTemplate = `<!DOCTYPE html>
<html>
<head>
<style>
body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
.header { background-color: #4CAF50; color: white; padding: 20px; text-align: center; }
.content { padding: 20px; background-color: #f9f9f9; }
.score-box { background: #e8f5e8; border-left: 4px solid #4CAF50; padding: 15px; margin: 15px 0; }
</style>
</head>
<body>
<div class="header">
<h1>ðŸŽ‰ Congratulations!</h1>
<h2>You've been shortlisted!</h2>
</div>
<div class="content">
<p>Dear {{name}},</p>
<p>Great news! You've been selected for the next round of our recruitment process.</p>
<div class="score-box">
<p><strong>Your ATS Score:</strong> {{score}}/100</p>
</div>
<p>We were impressed by your qualifications and you've successfully passed our initial screening.</p>
<p><strong>Next Steps:</strong></p>
<ul>
<li>You'll receive a phone call for a brief interview within 24-48 hours</li>
<li>Please keep your phone accessible during business hours</li>
<li>The interview will take approximately 10-15 minutes</li>
</ul>
<p>Best regards,<br>HR Team</p>
</div>
</body>
</html>`;

return shortlisted.map(candidate => {
  const personalizedEmail = emailTemplate
    .replace(/{{name}}/g, candidate.name)
    .replace(/{{score}}/g, candidate.ats_score);

  return {
    json: {
      candidate_id: candidate.candidate_id,
      name: candidate.name,
      email: candidate.email,
      subject: `Congratulations! You've been shortlisted - ${candidate.name}`,
      html_content: personalizedEmail,
      type: 'shortlisted'
    }
  };
});

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const rejectedCandidates = staticData.candidates.filter(c => 
  c.status === 'ats_rejected' && !c.rejection_email_sent
);

if (rejectedCandidates.length === 0) {
  return [];
}

rejectedCandidates.forEach(candidate => {
  const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    staticData.candidates[candidateIndex].rejection_email_sent = true;
    staticData.candidates[candidateIndex].rejection_email_date = new Date().toISOString();
  }
});

const emailTemplate = `<!DOCTYPE html>
<html>
<head>
<style>
body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
.header { background-color: #2196F3; color: white; padding: 20px; text-align: center; }
.content { padding: 20px; background-color: #f9f9f9; }
</style>
</head>
<body>
<div class="header">
<h1>Thank You for Your Application</h1>
</div>
<div class="content">
<p>Dear {{name}},</p>
<p>Thank you for your interest in our company and for taking the time to apply for the position.</p>
<p>After careful consideration, we have decided to move forward with other candidates whose qualifications more closely match our current requirements.</p>
<p>We encourage you to apply for future positions that may be a better fit for your skills and experience.</p>
<p>We will keep your profile on file and may contact you about suitable opportunities in the future.</p>
<p>Thank you again for your interest in our company.</p>
<p>Best regards,<br>HR Team</p>
</div>
</body>
</html>`;

return rejectedCandidates.slice(0, 20).map(candidate => {
  const personalizedEmail = emailTemplate.replace(/{{name}}/g, candidate.name);

  return {
    json: {
      candidate_id: candidate.candidate_id,
      name: candidate.name,
      email: candidate.email,
      subject: `Thank you for your application - ${candidate.name}`,
      html_content: personalizedEmail,
      type: 'rejected'
    }
  };
});

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

if (!staticData.candidates) {
  return [{
    json: {
      success: false,
      message: 'No candidates found',
      interviews: []
    }
  }];
}

const pendingInterviews = staticData.candidates.filter(c => 
  c.status === 'shortlisted' && !c.interview_scheduled
);

if (pendingInterviews.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No pending interviews to schedule',
      interviews: []
    }
  }];
}

const interviewQuestions = [
  {
    question: "Can you tell me about yourself and your professional background?",
    category: "introduction",
    expected_keywords: ["experience", "background", "skills", "career"],
    max_score: 20
  },
  {
    question: "Why are you interested in this position?", 
    category: "motivation",
    expected_keywords: ["interest", "company", "role", "opportunity"],
    max_score: 20
  },
  {
    question: "What are your greatest strengths?",
    category: "strengths",
    expected_keywords: ["strengths", "skills", "abilities", "expertise"],
    max_score: 20
  },
  {
    question: "Describe a challenging situation you faced at work and how you handled it.",
    category: "problem_solving",
    expected_keywords: ["challenge", "problem", "solution", "handled"],
    max_score: 20
  },
  {
    question: "Where do you see yourself in the next 5 years?",
    category: "future_goals",
    expected_keywords: ["goals", "future", "career", "growth"],
    max_score: 20
  }
];

if (!staticData.interviews) {
    staticData.interviews = [];
}

const toSchedule = pendingInterviews.slice(0, 10);
const scheduled = [];

toSchedule.forEach((candidate, index) => {
  const interviewId = `interview_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  const scheduledTime = new Date();
  scheduledTime.setMinutes(scheduledTime.getMinutes() + (index + 1) * 5);

  const interview = {
    interview_id: interviewId,
    candidate_id: candidate.candidate_id,
    candidate_name: candidate.name,
    candidate_phone: candidate.phone,
    candidate_email: candidate.email,
    scheduled_time: scheduledTime.toISOString(),
    status: 'scheduled',
    questions: interviewQuestions,
    ats_score: candidate.ats_score
  };

  scheduled.push(interview);
  staticData.interviews.push(interview);

  const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    staticData.candidates[candidateIndex].interview_scheduled = true;
    staticData.candidates[candidateIndex].interview_id = interviewId;
    staticData.candidates[candidateIndex].interview_scheduled_time = scheduledTime.toISOString();
    staticData.candidates[candidateIndex].status = 'interview_scheduled';
  }
});

return scheduled.map(interview => ({ json: { interview } }));

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const interviewData = $input.first().json.interview;

const mockResponses = [
  "I have 5 years of experience in software development, working with various technologies like React and Node.js.",
  "I'm interested in this position because it aligns with my career goals and I admire the company's innovative approach.",
  "My greatest strength is problem-solving and my ability to work effectively in team environments.",
  "I once faced a critical bug in production and managed to identify and fix it within 2 hours through systematic debugging.",
  "In 5 years, I see myself in a senior technical role, leading projects and mentoring junior developers."
];

const responses = interviewData.questions.map((q, index) => {
  const response = mockResponses[index] || "Candidate provided a brief response.";

  let score = 0;
  const responseWords = response.toLowerCase().split(/\s+/);
  const matchedKeywords = q.expected_keywords.filter(keyword => 
    responseWords.some(word => word.includes(keyword.toLowerCase()))
  );

  score = Math.min((matchedKeywords.length / q.expected_keywords.length) * q.max_score, q.max_score);

  if (response.length > 50) score += 2;
  if (response.length > 100) score += 2;

  return {
    question: q.question,
    response: response,
    expected_keywords: q.expected_keywords,
    matched_keywords: matchedKeywords,
    score: Math.round(score),
    max_score: q.max_score
  };
});

const totalScore = responses.reduce((sum, r) => sum + r.score, 0);
const maxPossibleScore = responses.reduce((sum, r) => sum + r.max_score, 0);
const interviewScore = Math.round((totalScore / maxPossibleScore) * 100);

const communicationScore = Math.min(interviewScore + Math.floor(Math.random() * 10), 100);
const technicalScore = Math.min(interviewScore + Math.floor(Math.random() * 15), 100);

const interviewResult = {
  interview_id: interviewData.interview_id,
  candidate_id: interviewData.candidate_id,
  call_id: `simulated_call_${Date.now()}`,
  call_status: 'completed',
  interview_date: new Date().toISOString(),
  responses: responses,
  communication_score: communicationScore,
  technical_score: technicalScore,
  overall_interview_score: interviewScore,
  total_possible_score: maxPossibleScore,
  actual_score: totalScore
};

const interviewIndex = staticData.interviews.findIndex(i => i.interview_id === interviewData.interview_id);
if (interviewIndex !== -1) {
  staticData.interviews[interviewIndex] = { ...staticData.interviews[interviewIndex], ...interviewResult };
  staticData.interviews[interviewIndex].status = 'completed';
}

const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === interviewData.candidate_id);
if (candidateIndex !== -1) {
  staticData.candidates[candidateIndex].interview_score = interviewScore;
  staticData.candidates[candidateIndex].interview_completed = true;
  staticData.candidates[candidateIndex].interview_date = interviewResult.interview_date;
  staticData.candidates[candidateIndex].status = 'interviewed';

  const atsScore = staticData.candidates[candidateIndex].ats_score || 0;
  const combinedScore = Math.round((atsScore * 0.6) + (interviewScore * 0.4));
  staticData.candidates[candidateIndex].combined_score = combinedScore;
}

return [{
  json: {
    success: true,
    interview_id: interviewResult.interview_id,
    candidate_id: interviewResult.candidate_id,
    interview_score: interviewScore,
    interview_result: interviewResult
  }
}];

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

if (!staticData.candidates) {
  return [{
    json: {
      success: false,
      message: 'No candidates found',
      selected: [],
      rejected: []
    }
  }];
}

const interviewedCandidates = staticData.candidates.filter(c => 
  c.status === 'interviewed' && c.combined_score !== undefined && c.combined_score > 0
);

if (interviewedCandidates.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No interviewed candidates found for final selection',
      selected: [],
      rejected: []
    }
  }];
}

interviewedCandidates.sort((a, b) => b.combined_score - a.combined_score);

const selectedCandidates = interviewedCandidates.slice(0, 20);
const rejectedCandidates = interviewedCandidates.slice(20);

interviewedCandidates.forEach((candidate, index) => {
  const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    if (index < 20) {
      staticData.candidates[candidateIndex].status = 'final_selected';
      staticData.candidates[candidateIndex].final_selection_date = new Date().toISOString();
    } else {
      staticData.candidates[candidateIndex].status = 'interview_rejected';
      staticData.candidates[candidateIndex].interview_rejection_date = new Date().toISOString();
    }
  }
});

const selectedSummary = selectedCandidates.map(c => ({
  candidate_id: c.candidate_id,
  name: c.name,
  email: c.email,
  phone: c.phone,
  ats_score: c.ats_score,
  interview_score: c.interview_score,
  combined_score: c.combined_score
}));

return [{
  json: {
    success: true,
    message: `Selected ${selectedCandidates.length} final candidates, rejected ${rejectedCandidates.length} after interview`,
    selected: selectedSummary,
    rejected_count: rejectedCandidates.length,
    total_processed: interviewedCandidates.length
  }
}];

const selected = $input.first().json.selected;

if (!selected || selected.length === 0) {
  return [];
}

