return selected.map(candidate => {
  const personalizedEmail = finalSelectedTemplate
    .replace(/{{name}}/g, candidate.name)
    .replace(/{{ats_score}}/g, candidate.ats_score)
    .replace(/{{interview_score}}/g, candidate.interview_score)
    .replace(/{{combined_score}}/g, candidate.combined_score);

  return {
    json: {
      candidate_id: candidate.candidate_id,
      name: candidate.name,
      email: candidate.email,
      subject: `ðŸŽ‰ Congratulations! Final Interview/Job Offer - ${candidate.name}`,
      html_content: personalizedEmail,
      type: 'final_selected'
    }
  };
});

const workflow = this.getWorkflow();
const staticData = workflow.staticData;

const rejectedCandidates = staticData.candidates.filter(c => 
  c.status === 'interview_rejected' && !c.final_rejection_email_sent
);

if (rejectedCandidates.length === 0) {
  return [];
}

rejectedCandidates.forEach(candidate => {
  const candidateIndex = staticData.candidates.findIndex(c => c.candidate_id === candidate.candidate_id);
  if (candidateIndex !== -1) {
    staticData.candidates[candidateIndex].final_rejection_email_sent = true;
    staticData.candidates[candidateIndex].final_rejection_email_date = new Date().toISOString();
  }
});

const finalRejectionTemplate = `<!DOCTYPE html>
