return rejectedCandidates.slice(0, 10).map(candidate => {
  const personalizedEmail = finalRejectionTemplate
    .replace(/{{name}}/g, candidate.name)
    .replace(/{{ats_score}}/g, candidate.ats_score)
    .replace(/{{interview_score}}/g, candidate.interview_score)
    .replace(/{{combined_score}}/g, candidate.combined_score);

  return {
    json: {
      candidate_id: candidate.candidate_id,
      name: candidate.name,
      email: candidate.email,
      subject: `Thank you for participating in our interview process - ${candidate.name}`,
      html_content: personalizedEmail,
      type: 'final_rejected'
    }
  };
});
